import React, { Component } from 'react';

export default class UserName extends Component {

	state = {
		eventAppeared: ''
	}



	render() {

		const onEventChange = (e) => {
			this.setState({
				eventAppeared: e.target.value
		   });
		}


		const onSubmit =(e) =>{
			e.preventDefault()
			this.props.setBlue(this.state.eventAppeared)
		}

		return (
						<form className='form-group username-form'>
							<div className="d-flex">
								<div className="dropdown">
								<select>
									<option className="dropdown-item" value="low">choose level</option>
									<option className="dropdown-item" value="low">Low</option>
									<option className="dropdown-item" value="average">Average</option>
									<option className="dropdown-item" value="High">High</option>
								</select>

								<input placeholder='user Name'
									 className="userName"
									 type='text'
									 onChange={onEventChange}/>
							</div>
								<div>
							<button onClick={onSubmit} type="submit" value="Submit" className="btn btn-secondary btn-sm">PLAY</button>
								</div>
								</div>
						</form>

		)
	}
}
