import React, { Component } from 'react';

export default class UserName extends Component {

	state = {
		eventAppeared: ''
	}



	async componentDidMount() {



//	const	dataWinner = {
//			"winner":
//					this.props.computerScores > 12 ? "Computer" : ""
//					||
//					this.props.userPoints > 12 ? this.state.yourNameState : ""   ,
//
//			"date": new Date().getHours() +":"+ new Date().getMinutes() +"; " +
//					new Date().getDate() + " "+ (new Date().toLocaleString("en-us", { month: "long" })) +
//					" " + new Date().getFullYear()
//		}
//
//		   const response = await fetch('https://starnavi-frontend-test-task.herokuapp.com/winners', {
//		      method: 'POST',
//		      headers: {
//		         'Content-Type': 'application/json;charset=utf-8'
//		      },
//		      body: JSON.stringify(dataWinner)
//			   });

//		console.log (response);










	dataTransfer = this.props.dataTransfer;

	onEventChange = (e) => {
		this.setState({
			eventAppeared: e.target.value
		});
		this.dataTransfer(e.target.value);
	}



	onSubmit =(e) => {
		e.preventDefault();
		this.props.setBlue(this.state.eventAppeared)

		let response = fetch('https://starnavi-frontend-test-task.herokuapp.com/winners', {
			method: 'POST',

			body: JSON.stringify({
				"winner":
						this.props.computerScores > 12 ? "Computer" : ""
						||
						this.props.userPoints > 12 ? this.state.eventAppeared(this.props.yourNameState) : "",

				"date": new Date().getHours() + ":" + new Date().getMinutes() + "; " +
						new Date().getDate() + " " + (new Date().toLocaleString("en-us", {month: "long"})) +
						" " + new Date().getFullYear()
			}),
			headers: {
				'Content-Type': 'application/json;charset=utf-8'
			}
		})
	}


	}











	render() {





		return (
						<form className='form-group username-form'>
							<div className="d-flex">
							<label>
								<input placeholder='user-Name'
									 className="form-control"
									 type='text'
									 onChange={this.onEventChange}/>
							</label>

								<div>
							<input type="submit"
									 value = {this.props.computerScores > 12
							||
							this.props.userPoints > 12 ? "PLAY AGAIN" : "PLAY"}
									 className="btn btn-secondary btn"
									  onClick={this.onSubmit}/>
								</div>
								</div>
						</form>

		)
	}
}
