import React, { Component } from 'react';
import Game from './components/Game/game';
import UserName from './components/UserName/username';



export default class App extends Component {

        state = {
            sizeEasyMode: undefined,
            sizeNormalMode: undefined,
            sizeHardMode: undefined,

            delayTimeEasyMode: undefined,
            delayTimeNormalMode: undefined,
            delayTimeHardMode: undefined,


        }

    async componentDidMount () {
        const response = await
            fetch(`https:starnavi-frontend-test-task.herokuapp.com/game-settings`
            )
        const gameSettings = await response.json();

        this.setState({
            sizeEasyMode: gameSettings.easyMode.field,
            sizeNormalMode: gameSettings.normalMode.field,
            sizeHardMode: gameSettings.hardMode.field,

            delayTimeEasyMode: gameSettings.easyMode.delay,
            delayTimeNormalMode: gameSettings.normalMode.delay,
            delayTimeHardMode: gameSettings.hardMode.delay,

        })
}
    render (){
        const duel = ['duel1', 'duel2', 'duel3', 'duel4', 'duel5', 'duel6', 'duel7',
            'duel8', 'duel9', 'duel10', 'duel11', 'duel12', 'duel13', 'duel14',
            'duel15', 'duel16', 'duel17', 'duel18', 'duel19', 'duel20', 'duel21',
            'duel22', 'duel23', 'duel24', 'duel25'];

        let numRandom = () => {
            let randomNumber = duel[Math.round(Math.random() * 24 + 1)];
        let rancelBlue = document.getElementById(randomNumber).style.backgroundColor='blue'
            setTimeout(numRandom,7000);
        };




        return (
            <div>
            <div className="d-flex">
                <UserName />
                <div>
                    <button onClick={numRandom}>Play</button>
                </div>
            </div>
                <div  style = {{
                    height: this.state.sizeEasyMode*5+"rem",
                    width: this.state.sizeEasyMode*5+"rem",
                }}  >
                < Game
                    sizeEasyMode={this.state.sizeEasyMode}
                    sizeNormalMode={this.state.sizeNormalMode}
                    sizeHardMode={this.state.sizeHardMode}

                    delayTimeEasyMode={this.state.delayTimeEasyMode}
                    delayTimeNormalMode={this.state.delayTimeNormalMode}
                    delayTimeHardMode={this.state.delayTimeHardMode}
                />
                </div>
            </div>
        )
    }
}
