import React, { Component } from 'react';

export default class UserName extends Component {

	state = {
		eventAppeared: ''
	}

	dataTransfer=this.props.dataTransfer;

	setBlue = this.props.setBlue;

	onEventChange = (e) => {
		this.setState({
			eventAppeared: e.target.value
		});
		this.dataTransfer(e.target.value);
	}

	onSubmit =(e) =>{
		e.preventDefault()
		this.setBlue(this.state.eventAppeared)
	}

	render() {

		console.log (this.state.eventAppeared);


		return (
						<forma className='form-group username-form'>

								<input placeholder='user-Name'
									 className="userName"
									 type='text'
									 onChange={this.onEventChange}/>


							<input type="submit" value="PLAY"
									 className="btn btn-secondary btn-sm"
									  onClick={this.onSubmit}/>


						</forma>

		)
	}
}
