import React, { Component } from 'react';

export default class UserName extends Component {

	state = {
		eventAppeared: ''
	}

	render() {


		const setBlue = this.props.setBlue

		const eventInput=this.props.eventInput
		eventInput=e.target.value

		const onEventChange = (e,props) => {
			this.setState({
				eventAppeared: e.target.value
			});
		}
		console.log(this.state.eventAppeared)

		const onSubmit =(e) =>{
			e.preventDefault()
			this.props.setBlue(this.state.eventAppeared)
		}


		return (
				<div>
					<div className="d-flex">
						<form>
							<label>choose level: </label>
							<select>
								<option value="low">Low</option>
								<option value="average">Average</option>
								<option value="High">High</option>
							</select>
						</form>

						<form className='username-form'
								onSubmit={this.props.onSubmit}>
							<input placeholder='user Name'
									 className="form-control"
									 type='text'
									 onChange={this.onEventChange}/>
						</form>

					</div>
				</div>
		)
	}
}
