        const randomNumber = duel[Math.floor(Math.random() * 25)];

        const colorBlue = () =>{
            this.setState({bgBlue: document.getElementById(randomNumber).style.backgroundColor = 'blue'})
        }
        setTimeout(colorBlue,this.state.delayTimeEasyMode);








