import React from 'react';

const Description =() =>{
	return(
		<div>
			<h5> To start the game, set the difficulty level and enter the username.</h5>
		<h5>	Press PLAY.</h5>
			<h5> - After a certain period of time (depending
				on the chosen level of difficulty of the game),
				a random square in the field is highlighted in blue.</h5>
			<h5> <h2>-</h2> If you manage to click on the blue square
				during this time - it will turn green, you will
				get a dot (the installed counter will show dots),
				the field will change color to green </h5>
			<h5> - if not, the field turns red and the point goes to the computer </h5>
			<h5> - When you or a computer draws more than 12 squares in its color, that one becomes a winner. </h5>
			<h5> Between the control buttons and the playing field, an inscription appears that the player won (the name he entered) / computer. </h5>
		</div>
		);
	};




export default Description;
